(4460, 'en', 'default', '324a8e205fd6e1ed56142378c52931a8', 'Hovering over a row in the posts list will display action links that allow you to manage your post. You can perform the following actions:', NULL, NULL, 'LINE', NULL, 0, '', 'fec1189b4b1f2a26e07f6552c036bacd', '', NULL),
(4461, 'en', 'default', '0bdd62cf14b1ac2df851e97eed7faa8c', '<strong>Edit</strong> takes you to the editing screen for that post. You can also reach that screen by clicking on the post title.', NULL, NULL, 'LINE', NULL, 0, '', '74deb9541548900791736df7e47727d1', '', NULL),
(4462, 'en', 'default', '099f3df0632488f2c47e2658d95e0e76', '<strong>Quick Edit</strong> provides inline access to the metadata of your post, allowing you to update post details without leaving this screen.', NULL, NULL, 'LINE', NULL, 0, '', 'aec7db39f9755f7c2dc6e2b37c8412c2', '', NULL),
(4463, 'en', 'default', '1ac12183049e2cd887bb8ee6b488d5fb', '<strong>Trash</strong> removes your post from this list and places it in the trash, from which you can permanently delete it.', NULL, NULL, 'LINE', NULL, 0, '', 'fb4aa500e080edf20572554e14786e36', '', NULL),
(4464, 'en', 'default', 'b807523847b38ac8553394a6e915ef62', '<strong>Preview</strong> will show you what your draft post will look like if you publish it. View will take you to your live site to view the post. Which link is available depends on your post&#8217;s status.', NULL, NULL, 'LINE', NULL, 0, '', 'feb9ddd4b67850aee6a2151805f1dfa8', '', NULL),
(4465, 'en', 'default', 'c83a2faddec053e93996bea49df84dd8', 'You can also edit or move multiple posts to the trash at once. Select the posts you want to act on using the checkboxes, then select the action you want to take from the Bulk Actions menu and click Apply.', NULL, NULL, 'LINE', NULL, 0, '', '95e6f8b591af95b7e141df1318db45fb', '', NULL),
(4466, 'en', 'default', '4fd5e879927f19de076cca45bc59434a', 'When using Bulk Edit, you can change the metadata (categories, author, etc.) for all selected posts at once. To remove a post from the grouping, just click the x next to its name in the Bulk Edit area that appears.', NULL, NULL, 'LINE', NULL, 0, '', 'd70e008926f797f7fa73820eeb9729d4', '', NULL),
