(300, 'thesis_classic_r_css', '// spatial variables to go with golden ratio typography\n// (you can reference these in your Custom CSS, too!)\n	$phi = 1.6180339887;\n\n// primary spacing\n	$x1 = $h5;\n	$x02 = round($x1 / $phi);\n	$x03 = round($x02 / $phi);\n	$x04 = round($x03 / $phi);\n\n// secondary (sidebar) spacing\n	$x1s = $sh5;\n	$x02s = round($x1s / $phi);\n	$x03s = round($x02s / $phi);\n	$x04s = round($x03s / $phi);\n\n// backwards compatibility variables for customizations\n	$f_aux = $f6;\n	$f_subhead = $f3;\n	$f_text = $f5;\n	$h_aux = $h6;\n	$h_text = $h5;\n	$x_half = $x02;\n	$x_single = $x1;\n	$x_3over2 = $x1 + $x02;\n	$x_double = 2 * $x1;\n	$s_x_half = $x02s;\n	$s_x_single = $x1s;\n	$s_x_3over2 = $x1s + $x02s;\n	$s_x_double = 2 * $x1s;\n\n/*---:[ layout structure ]:---*/\nbody {\n	font-family: $font;\n	font-size: $f5;\n	line-height: $h5;\n	color: $text1;\n	background-color: $color3;\n	padding-top: $x1;\n}\n.container {\n	width: $w_total;\n	margin: 0 auto;\n}\n.columns {\n	box-sizing: border-box;\n	> .content {\n		box-sizing: border-box;\n		width: $w_content;\n		$column1\n		border-style: solid;\n		border-color: $color1;\n	}\n	> .sidebar {\n		box-sizing: border-box;\n		$column2\n		padding: $x1 $x1 0 $x1;\n	}\n}\n/*---:[ links ]:---*/\na {\n	color: $links;\n	text-decoration: none;\n	p & {\n		text-decoration: underline;\n		&:hover {\n			text-decoration: none;\n		}\n	}\n}\n/*---:[ nav menu ]:---*/\n$menu_phi = $menu_f * $phi;\n$menu_x = round($menu_phi / $phi);\n$menu_y = round($menu_x / $phi);\n@mixin menu_links {\n	display: block;\n	$menu\n	text-transform: uppercase;\n	letter-spacing: 1px;\n	color: $text1;\n	background-color: $color2;\n	padding: $menu_y $menu_x;\n	border-width: 1px 1px 1px 0;\n	border-style: solid;\n	border-color: $color1;\n	&:hover {\n		background-color: $color1;\n	}\n}\n.menu {\n	position: relative;\n	z-index: 50;\n	list-style: none;\n	border-width: 0 0 1px 1px;\n	border-style: solid;\n	border-color: $color1;\n	a {\n		@include menu_links;\n	}\n	li {\n		position: relative;\n		float: left;\n		margin-bottom: -1px;\n	}\n	.sub-menu {\n		display: none;\n		position: absolute;\n		z-index: 110;\n		left: -1px;\n		list-style: none;\n		border-color: $color1;\n		margin-top: -1px;\n		.sub-menu {\n			top: 0;\n			left: $submenu;\n			margin: 0 0 0 -1px;\n		}\n		li {\n			width: $submenu;\n			clear: both;\n		}\n		a {\n			border-left-width: 1px;\n		}\n		.current-menu-item > a {\n			border-bottom-color: $color1;\n		}\n	}\n	li:hover > .sub-menu {\n		display: block;\n	}\n	.current-menu-item > a {\n		border-bottom-color: $color3;\n		background-color: $color3;\n		cursor: text;\n	}\n}\n.menu_control {\n	@include menu_links;\n	display: none;\n	background-color: $color3;\n}\n/*---:[ header ]:---*/\n.header {\n	border-bottom: 3px double $color1;\n	padding: $x1;\n}\n#site_title {\n	$title\n	line-height: 1.31em;\n	font-weight: bold;\n	color: $title_color;\n	a {\n		color: $title_color;\n		&:hover {\n			color: $links;\n		}\n	}\n	& + #site_tagline {\n		margin-top: $x04;\n	}\n}\n#site_tagline {\n	$tagline\n	line-height: 1.5em;\n}\n/*---:[ golden ratio typography with spaced paragraphs ]:---*/\n.grt {\n	font-size: $f5;\n	line-height: $h5;\n	h1, .headline {\n		$headline\n	}\n	h1 {\n		margin-bottom: $x1;\n	}\n	.headline {\n		color: $headline_color;\n		margin: 0;\n		a {\n			color: $headline_color;\n			&:hover {\n				color: $links;\n			}\n		}\n	}\n	h2, h3, h4 {\n		$subhead\n		color: $subhead_color;\n	}\n	h2 {\n		font-size: $f3;\n		line-height: $h3;\n		margin-top: ($x1 + $x02);\n		margin-bottom: $x02;\n	}\n	h3 {\n		font-size: $f4;\n		line-height: $h4;\n		margin-top: ($x1 + $x03);\n		margin-bottom: $x03;\n	}\n	h4 {\n		font-size: $f5;\n		line-height: $h5;\n		font-weight: bold;\n		margin-bottom: $x04;\n	}\n	h1 + h2, h2 + h3 {\n		margin-top: 0\n	}\n	.post_content {\n		h2, h3 {\n			&:first-child {\n				margin-top: 0;\n			}\n		}\n	}\n	ul {\n		list-style-type: square;\n		li a {\n			text-decoration: underline;\n			&:hover {\n				text-decoration: none;\n			}\n		}\n	}\n	blockquote {\n		$blockquote\n		padding-left: $x02;\n		border-left: 1px solid $color1;\n		&.right, &.left {\n			width: 45%;\n			$pullquote\n			padding-left: 0;\n			border: 0;\n			margin-bottom: $x02;\n		}\n	}\n	code, pre, kbd {\n		font-size: $f5 - 2;\n	}\n	code {\n		$code\n		background-color: rgba(0,0,0,0.08);\n		padding: round($x04 / 2) ($x04 - 2);\n		border-radius: $x04;\n		margin: 0 1px;\n	}\n	pre {\n		$pre\n		background-color: $color2;\n		padding: $x02 $x02 $x02 $x03;\n		border-left: $x04 solid rgba(0,0,0,0.15);\n	}\n	kbd {\n		font-family: Consolas, Menlo, Monaco, Courier, Verdana, sans-serif;\n		color: #111;\n		background-color: #fff;\n		padding: round($x04 / 2) ($x04 - 2);\n		border-radius: $x04;\n		box-shadow: 0 0 $x04 0 rgba(0,0,0,0.45);\n		margin: 0 1px;\n	}\n	.alert, .note, .box {\n		padding: $x02;\n	}\n	.alert {\n		background-color: #ff9;\n		border: 1px solid #e6e68a;\n	}\n	.note {\n		background-color: $color2;\n		border: 1px solid $color1;\n	}\n	$_links = $links;\n	.box {\n		background-color: scale-color($_links, $lightness: 90%);\n		border: 1px solid scale-color($_links, $lightness: -10%);\n	}\n	.footnotes {\n		font-size: $f6;\n		line-height: $h6;\n		padding-top: $x1;\n		border-top: 1px dotted $color1;\n	}\n	.footnotes, sub, sup, .post_cats, .post_tags {\n		color: $text2;\n	}\n	fieldset {\n		margin-bottom: $x1;\n		legend {\n			font-size: $f4;\n			line-height: $h4;\n			font-weight: bold;\n			margin-bottom: $x04;\n		}\n	}\n	.avatar {\n		$avatar\n		float: right;\n		clear: both;\n		margin-left: $x02;\n	}\n	.small, .caption {\n		font-size: $f6;\n		line-height: $h6;\n	}\n	.caption {\n		margin-top: -$x02;\n		color: $text2;\n	}\n	.frame, .post_image_box, .wp-caption {\n		box-sizing: border-box;\n		background-color: $color2;\n		padding: $x02;\n		border: 1px solid $color1;\n	}\n	.wp-caption p {\n		font-size: $f6;\n		line-height: $h6;\n	}\n	.wp-caption img, .post_image_box .post_image, .thumb, .footnotes p {\n		margin-bottom: $x02;\n	}\n	.drop_cap {\n		font-size: (2 * $x1);\n		line-height: 1em;\n		margin-right: $x03;\n		float: left;\n	}\n	.author_description {\n		padding-top: $x1;\n		border-top: 1px dotted $color1;\n		.avatar {\n			$bio_avatar\n			float: left;\n			margin-right: $x02;\n			margin-left: 0;\n		}\n	}\n	.author_description_intro {\n		font-weight: bold;\n	}\n	p, ul, ol, blockquote, pre, dl, dd, .center, .aligncenter, .block,  .alignnone, .post_image, .post_image_box, .wp-post-image, .caption, .wp-caption, .alert, .note, .box, .footnotes, .headline_area {\n		margin-bottom: $x1;\n	}\n	.right, .alignright, .ad {\n		margin-bottom: $x1;\n		margin-left: $x1;\n	}\n	.left, .alignleft, .ad_left {\n		margin-bottom: $x1;\n		margin-right: $x1;\n	}\n	ul, ol, .stack {\n		margin-left: $x1;\n	}\n	ul ul, ul ol, ol ul, ol ol, .wp-caption p, blockquote.right p, blockquote.left p {\n		margin-bottom: 0;\n	}\n	.alert, .note, .box, .right, .left .footnotes {\n		:last-child {\n			margin-bottom: 0;\n		}\n	}\n}\n/*---:[ other content styles ]:---*/\n.post_box {\n	padding: $x1 $x1 0 $x1;\n	border-top: 1px dotted $color1;\n	&.top {\n		border-top: 0;\n	}\n}\n.byline {\n	color: $text2;\n	a {\n		color: $text2;\n		border-bottom: 1px solid $color1;\n		&:hover {\n			color: $text1;\n		}\n	}\n	a, .post_author, .post_date {\n		text-transform: uppercase;\n		letter-spacing: 1px;\n	}\n	.post_author_intro, .post_date_intro, .post_cats_intro {\n		font-style: italic;\n	}\n	.post_edit {\n		margin-left: $x03;\n		&:first-child {\n			margin-left: 0;\n		}\n	}\n}\n.wp-caption {\n	&.aligncenter img {\n		margin-right: auto;\n		margin-left: auto;\n	}\n	.wp-caption-text .wp-smiley {\n		display: inline;\n		margin-bottom: 0;\n	}\n}\n.num_comments_link {\n	display: inline-block;\n	color: $text2;\n	text-decoration: none;\n	margin-bottom: $x1;\n	$_color1 = $color1;\n	$_c1l = lightness($_color1);\n	&:hover {\n		background-color: change-color($_color1, $lightness: $_c1l + ((100 - $_c1l) / 2));\n	}\n}\n.num_comments {\n	font-size: $x1;\n	color: $text1;\n}\n.bracket {\n	font-size: $x1;\n	color: $color1;\n}\n.archive_intro {\n	border-width: 0 0 1px 0;\n	border-style: solid;\n	border-color: $color1;\n	.headline {\n		margin-bottom: $x1;\n	}\n}\n.prev_next {\n	clear: both;\n	color: $text2;\n	border-top: 1px solid $color1;\n	padding: $x02 $x1;\n	.next_posts {\n		float: right;\n	}\n}\n.previous_posts, .next_posts {\n	display: block;\n	font-size: $f6;\n	line-height: $h6;\n	text-transform: uppercase;\n	letter-spacing: 2px;\n	a:hover {\n		text-decoration: underline;\n	}\n}\n/*---:[ comments ]:---*/\n#comments {\n	margin-top: (2 * $x1);\n}\n.comments_intro {\n	color: $text2;\n	padding: 0 $x1;\n	margin-bottom: $x02;\n	a:hover {\n		text-decoration: underline;\n	}\n}\n.comments_closed {\n	font-size: $f6;\n	line-height: $h6;\n	color: $text2;\n	margin: 0 $x1 $x1 $x1;\n}\n.comment_list {\n	list-style-type: none;\n	border-top: 1px dotted $color1;\n	margin-bottom: (2 * $x1);\n}\n.comment {\n	padding: $x1;\n	border-bottom: 1px dotted $color1;\n	.comment_head {\n		margin-bottom: $x02;\n	}\n	.comment_author {\n		font-weight: bold;\n	}\n	.avatar {\n		float: right;\n		$comment_avatar\n		margin-left: $x02;\n	}\n	.comment_date {\n		display: block;\n		font-size: $f6;\n		line-height: $h6;\n		color: $text2;\n		a {\n			color: $text2;\n		}\n	}\n	.comment_text > :last-child {\n		margin-bottom: 0;\n	}\n	.comment_footer {\n		margin-top: $x02;\n		a {\n			font-size: $f6;\n			line-height: $h6;\n			color: $text2;\n			text-transform: uppercase;\n			letter-spacing: 1px;\n			margin-right: $x02;\n		}\n	}\n	.comment_footer a {\n		font-size: $f6;\n		line-height: $h6;\n		color: $text2;\n		text-transform: uppercase;\n		letter-spacing: 1px;\n		margin-right: $x02;\n	}\n}\n.children {\n	.comment {\n		list-style-type: none;\n		padding: 0 0 0 $x1;\n		border-bottom: 0;\n		border-left: 1px solid $color1;\n		margin-top: $x1;\n	}\n	.bypostauthor {\n		background-color: transparent;\n		border-color: $links;\n	}\n}\n.comment_head, .comment_footer, comment_nav {\n	a:hover {