splay: inline;\n		margin-left: $x04;\n	}\n	#wp-comment-cookies-consent + label {\n		font-size: $f6;\n		line-height: $h6;\n		color: $text2;\n	}\n	input[type="text"] {\n		width: 50%;\n	}\n	input[type="submit"] {\n		font-size: $f4;\n	}\n	textarea {\n		display: block;\n		width: 100%;\n	}\n}\n#cancel-comment-reply-link {\n	float: right;\n	font-size: $f6;\n	line-height: inherit;\n	text-transform: uppercase;\n	letter-spacing: 1px;\n	color: $links;\n	margin-top: $x04;\n	&:hover {\n		text-decoration: underline;\n	}\n}\n.login_alert {\n	font-weight: bold;\n	background-color: $color2;\n	border: 1px solid $color1;\n}\n/*---:[ sidebar ]:---*/\n.sidebar {\n	$sidebar\n	.widget_title, .sidebar_heading, .headline {\n		$sidebar_heading\n		margin-bottom: $x02s;\n	}\n	.widget_title, .sidebar_heading {\n		font-variant: small-caps;\n		letter-spacing: 1px;\n	}\n	p, ul, ol, blockquote, pre, dl, dd, .left, .alignleft, .ad_left, .right, .alignright, .ad, .center, .aligncenter, .block, .alignnone {\n		margin-bottom: $x1s;\n	}\n	.left, .alignleft, .ad_left {\n		margin-right: $x1s;\n	}\n	ul ul, ul ol, ol ul, ol ol, .right, .alignright, .ad, .stack {\n		margin-left: $x1s;\n	}\n	ul ul, ul ol, ol ul, ol ol, .wp-caption p, .post_excerpt p {\n		margin-bottom: 0;\n	}\n	.text_box, .thesis_email_form, .query_box {\n		margin-bottom: (2 * $x1s);\n	}\n	.search-form, .thesis_email_form {\n		input[type="text"] {\n			width: 100%;\n			margin-bottom: $x02s;\n		}\n	}\n	button, input[type="submit"] {\n		padding: $x03s;\n	}\n	.query_box .post_author, .query_box .post_date {\n		color: $text2;\n	}\n	.widget {\n		margin-bottom: (2 * $x1s);\n		ul {\n			list-style-type: none;\n			li {\n				margin-bottom: $x02s;\n				ul, ol {\n					margin-top: $x02s;\n				}\n				a:hover {\n					text-decoration: underline;\n				}\n			}\n		}\n	}\n}\n/*---:[ footer ]:---*/\n.footer {\n	font-size: $f6;\n	line-height: $h6;\n	color: $text2;\n	text-align: right;\n	padding: $x02 $x1;\n	border-top: 3px double $color1;\n	a {\n		color: $text2;\n	